mapper.select:
SELECT A.INTERVIEW_SUGGESTION_CODE_PK , A.DIVISTION , B.COM_NAME , B.COM_URL , A.INTERVIEW_DATE , A.SUGGESTION_RESPONSE FROM TBL_MT_INTERVIEW_SUGGESTION A JOIN TBL_COM_COMPANY B ON (A.COMPANY_CODE_FK = B.COMPANY_CODE_PK) WHERE A.MEMBER_CODE_FK = #{memberCode}
AND B.COM_NAME LIKE '%'||#{content}||'%'
ORDER BY A.INTERVIEW_DATE OFFSET ${page} ROWS FETCH NEXT 10 ROWS ONLY

mapper.update:
UPDATE TBL_MT_INTERVIEW_SUGGESTION A
   SET A.SUGGESTION_RESPONSE = #{response}
 WHERE A.INTERVIEW_SUGGESTION_CODE_PK = #{interviewSuggestionCode}
