 , RECORDED_SCORE
)
VALUES
(
    SEQ_RSM_EDUCATAION_CODE_PK.NEXTVAL, #{schoolDivision}, #{schoolName}
  , #{schoolStartDate}, #{schoolEndDate}, #{major}
  , #{schoolStatus}, SEQ_RSM_RESUME_CODE_PK.CURRVAL, #{totalScore}
  , #{recordedScore}
)

